;--------------------------------------------------------------------------------------
; Sokoban autogenerated map data from standard maps                                    
; Check http://borgar.net/programs/sokoban/levels/Sokoban.html for more original levels
;                                                                                      
; Converter in Python by Daniel Leon (UFV) for AOC 2021/2022                           
;--------------------------------------------------------------------------------------
; MAP ENCODING:                                                                        
;   bit 7: (0): no Wall tile, (1) Wall tile                                            
;   bit 6: (0): tile is not a destination, (1) tile is a box destination (goal)        
;   bit 5: (0): tile does not have a box, (1) tile has a box                           
;   bit 4: (0): tile does not contain player, (1) tile contains player                 
;   bit 3: (0): tile is valid, (1) Tile is exterior                                    
;   bits 2..0 : Reserved                                                               
;--------------------------------------------------------------------------------------


maplist: DEFW level1, level2, 0

level1: DEFB 5, 19  ; Map Size Y, Size X
        DEFB 2, 1  ; Initial man position Y,X
        DEFB 1  ; number of goals for this level
        DEFB $08, $08, $08, $08, $08, $08, $08, $08, $80, $80, $80, $80, $80, $80, $80, $80, $80, $80, $80  ; Map data, 1 DEFB per map row
        DEFB $80, $80, $80, $80, $80, $80, $80, $80, $80, $00, $00, $00, $00, $00, $00, $00, $00, $40, $80
        DEFB $80, $10, $00, $00, $00, $00, $00, $00, $00, $00, $20, $80, $80, $80, $80, $80, $80, $80, $80
        DEFB $80, $80, $80, $80, $80, $80, $80, $80, $80, $00, $00, $80, $08, $08, $08, $08, $08, $08, $08
        DEFB $08, $08, $08, $08, $08, $08, $08, $08, $80, $80, $80, $80, $08, $08, $08, $08, $08, $08, $08

        ;               ###########
        ;       #########        .#
        ;       #@        $########
        ;       #########  #
        ;               #### 


level2: DEFB 11, 19  ; Map Size Y, Size X
        DEFB 8, 11  ; Initial man position Y,X
        DEFB 6  ; number of goals for this level
        DEFB $08, $08, $08, $08, $80, $80, $80, $80, $80, $08, $08, $08, $08, $08, $08, $08, $08, $08, $08  ; Map data, 1 DEFB per map row
        DEFB $08, $08, $08, $08, $80, $00, $00, $00, $80, $08, $08, $08, $08, $08, $08, $08, $08, $08, $08
        DEFB $08, $08, $08, $08, $80, $20, $00, $00, $80, $08, $08, $08, $08, $08, $08, $08, $08, $08, $08
        DEFB $08, $08, $80, $80, $80, $00, $00, $20, $80, $80, $08, $08, $08, $08, $08, $08, $08, $08, $08
        DEFB $08, $08, $80, $00, $00, $20, $00, $20, $00, $80, $08, $08, $08, $08, $08, $08, $08, $08, $08
        DEFB $80, $80, $80, $00, $80, $00, $80, $80, $00, $80, $08, $08, $08, $80, $80, $80, $80, $80, $80
        DEFB $80, $00, $00, $00, $80, $00, $80, $80, $00, $80, $80, $80, $80, $80, $00, $00, $40, $40, $80
        DEFB $80, $00, $20, $00, $00, $20, $00, $00, $00, $00, $00, $00, $00, $00, $00, $00, $40, $40, $80
        DEFB $80, $80, $80, $80, $80, $00, $80, $80, $80, $00, $80, $10, $80, $80, $00, $00, $40, $40, $80
        DEFB $08, $08, $08, $08, $80, $00, $00, $00, $00, $00, $80, $80, $80, $80, $80, $80, $80, $80, $80
        DEFB $08, $08, $08, $08, $80, $80, $80, $80, $80, $80, $80, $08, $08, $08, $08, $08, $08, $08, $08

        ;           #####
        ;           #   #
        ;           #$  #
        ;         ###  $##
        ;         #  $ $ #
        ;       ### # ## #   ######
        ;       #   # ## #####  ..#
        ;       # $  $          ..#
        ;       ##### ### #@##  ..#
        ;           #     #########
        ;           ####### 